// Test generated by RoostGPT for test golang-level0-PassGen using AI Type Open AI and AI Model gpt-4

package main

import (
	"strings"
	"testing"
)

func TestGeneratePassword(t *testing.T) {
	tests := []struct {
		name   string
		length int
	}{
		{"Test Length 10", 10},
		{"Test Length 20", 20},
		{"Test Length 30", 30},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			password := generatePassword(tt.length)

			if len(password) != tt.length {
				t.Errorf("Expected length: %v, got: %v", tt.length, len(password))
			}

			digits := "0123456789"
			specials := "~=+%^*/()[]{}/!@#$?|"

			if !strings.ContainsAny(password, digits) {
				t.Errorf("Password does not have any digits")
			}

			if !strings.ContainsAny(password, specials) {
				t.Errorf("Password does not have any special characters")
			}

			t.Logf("Generated password: %v", password)
		})
	}
}
